import "./App.css"
import React from "react"//引入 react 包


//事件对象 e 


//比如组织 a 标签的跳转
function Hello() {

  const clickMe = (e) => {
    console.log(e.target);
    console.log(e.currentTarget);
  }

  return <div onClick = { clickMe } > 
    <a href="http://www.google.com"></a>

  Hello </div>
}



//二：渲染类组件
function App () {
  return (
      <div>
        <Hello/>

      </div>

  )
}


//最后需要 export 一下！
export default App
