import "./App.css"
import React from "react"//引入 react 包


//写法三 🚀🚀🚀 标准的写法
class Test extends React.Component {


  handler = () => { //🍎：用箭头函数的写法来明确 this 
    console.log(this)
  }

  render() {
      return(
        <button onClick={ this.handler }>点击我</button>
      )
  }
}


//渲染类组件(根组件)
function App () {
  return (
      <div>
        <Test/>
      </div>

  )
}


//最后需要 export 一下！
export default App
