// import logo from './logo.svg';
// import './App.css';


//基础表达式一：常规的变量
const name = '饭团'

//基础表达式二：常规方法
const getAge = () =>{
  return 18 + '岁'
}

//基础表达式三：用于三元运算符
const flag = true



//列表渲染 - 原生的  map 方法
const songs = [
  { id: 1, name: '歌曲A'},
  { id: 2, name: '歌曲B'},
  { id: 3, name: '歌曲C'},
]




function App() {
  return (
    <div className="App">

      {/* 下面都是在渲染变量或者函数的返回值 */}
      { name }
      { getAge() }
      { flag ? '真棒':'很菜' }

      <ul>
        {/* 👇拿到歌曲列表, map(x=>x.y)用 map 来遍历, song 可以自定义，=> 箭头后面为要渲染出来的节点*/}
        {songs.map(song=><li key={song.id}>{song.name}</li>)} 

      </ul>





    </div>
  );
}

export default App;
