import "./App.css"
import React from "react"//引入 react 包


//⚡️⚡️⚡️ 组件通信(让组件内的数据 state 进行可以进行流转)
//🍎父子关系

  //HOW (子传父）
        //1.先子传父，再父传子
        //2.比如 SonA 传给 Father , Father 再传给 SonB



//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————————


//🍎类组件 Father
class Father extends React.Component {
  getMsg = (msg) => {
    console.log(msg);
  }

  render() {
    return (
      <div>
        <SonA 
          getMsg = {this.getMsg}
        />
        <br />
        <SonB />
      </div>
    );
  }
};

//——————————————————————



function SonA (){
  return(
    <div>
      这是 A 子组件
    </div>
  )
}


//——————————————————————


function SonB (props){
  const { getMsg } = props

  function clickFn (){

    const xx = '哈哈'
    getMsg(xx)
  }

  return(
    <div>
        这是 B 子组件
        <button onClick={ clickFn }>获得子组件 B 的数据</button>
    </div>
  )
}


//最后需要 export 一下！
export default Father
