import "./App.css"
import React from 'react'
//1.从 react 中导入 useState,useEffect 函数
import { useState,useEffect } from 'react'


//🚀 Hook - useState 回调函数生成初始值的写法 🔥


function Counter (){
  const [count,setCount] = useState(0)
  return(
    <button 
      onClick={()=> setCount(count+1)}> {count} </button>
  )
}

function APP(){
  return(
    <div></div>
  )
}

// 最后需要 export 一下！
export default APP
