import "./App.css"
import React from 'react' 

//🚀 函数组件跟类组件的默认值




//🚀 

//🍎子组件：渲染列表结构
function Test( {list} ){
  return (
    <div>
      {list.map( item => <p> {item} </p>)}
    </div>
  )
}


//校验 Test, 让传入 list 的参数必须为数组 [] 类型的参数
Test.PropType = {
  //定义各种各样的规则
  list: PropType.array.isRequired  //鉴定传入 list 的参数必须为数组 isRequired 为必传
}


//🍎父组件：提供数据
class Data extends React.Component {
  render() {
    return (
      <div>
        {/* 👇 给 List 传参 */}
        <Test 
          list={[1,2]} //传入数组
        />
      </div>
    )
  }

};

// //——————————————————————


// //最后需要 export 一下！
export default Data
