import "./App.css"
import React from 'react'
//1.从 react 中导入 useState,useEffect 函数
import { useState,useEffect } from 'react'


//🚀 Hook - useEffect 清除副作用



function Test (){ 
  return(
    <div>我是结果</div>

  )
}


function APP(){

  const [flag,setFlag] = useState()
  return(
    <div>
      {flag ? <Test /> : null}}
      <button onClick={() =>setFlag(!flag)}> 函数的销毁&重建 </button>
    </div>
  )
}

// 最后需要 export 一下！
export default APP
