import "./App.css"
import React from 'react' 
import PropType from 'prop-types'

//🚀【函数组件】跟【类组件】的 Props 默认值





//🍎 函数组件的默认值
  //方法一： Test.defaultProps
  //方法二：{pageSize=10}
function Test () {
  return (
    <div>
      这是一个函数组件
    </div>
  )
}


//设置默认值
Test.defaultProps = {
  pageSize: 10 //🔥🔥如果不传 pageSize ，则默认值为 10
}

// //——————————————————————
class App extends React.Component {
  render () {
    return (
      <div>
        <Test 
          pageSize={20}
        />
      </div>
    )
  }
}


// //最后需要 export 一下！
export default App
