import "./App.css"
import React from 'react'
//1.从 react 中导入 useState,useEffect 函数
import { useState } from 'react'


// Hook:// - useWindowScroll

    //What?
        //获取滚动距离 Y

    //How?
        //const [y] = useWindowScroll()


//🚀 Hook - useLocalStorage

  //What?
    //1.可以通过函数的参数，自定义传入 useState 的默认值
    //2.每次只要修改 message 数据的时候，都会自动的向本地同步一份

    //How?
    //const [message,setMessage] = useLocalStorage(defaultValue)






//——————————————————————————————————————————————————————————————————



function useWindowScroll () {

  const [y, setY] = useState(0)

  //在滚动行为发生的时候，不断的获取滚动值，并交给 y
  window.addEventListener('scroll', () => {
    //监控滚动的【距离】
    const deltaY = document.documentElement.scrollTop
    setY(deltaY) //把【距离】赋值给 y，直接传参就可以了🔥
  })

  return [y]
}


function APP () {
  const [y] = useWindowScroll()
  return (
    <div style={ {height:'12000px'} }>
      <p>
        滚动距离：{y}
      </p>
    </div>
  )
}



