import React from 'react' 

//🚀

//🍎子组件：渲染列表结构
function ListItem( {children} ){

  // children()   //执行 children 的函数

  return (
    <>
    <h3>小标题</h3>    
    <p>
      啦啦啦,
      {/* 执行 children 内的一些元素，比如 div */}
      {/* {children} */} 
    </p>
    {/* <div>{children}</div>  */}

    {/* 如果 children 是一组元素的话，可以通过遍历来取得里边的元素 */}
    {children.map(child => child)}
    </>
  )
}



//🍎父组件：提供数据
class Data extends React.Component {

  render() {
    return (

      <div>

      </div>
    )
  }

};

