import "./App.css"
import React from "react"//引入 react 包

//组件化：函数组件 & 类组件
//原则：
// 🚀🚀 



//一：创建类组件
class HelloComponent extends React.Component {
  render() {
      return <div>这是一个类组件</div>
  }
}


//二：渲染函数组件
function App () {
  return (
      <div>

        {/* 自闭合标签 */}
          <Hello />

        {/* 函数式标签 */}
        <HelloComponent></HelloComponent>

      </div>

  )
}


//最后需要 export 一下！
export default App
