class Test extends React.Component {
  render() {
    return (
       <div>
         {this.props.wellDone}
       </div>
    );
  }
}

//定义类组件的默认值
Test.defaultProps = {
  wellDone: true,
}


class App extends React.Component {
  render() {
    return (
      <Test/>
    )
  }
}




