//样式控制(把样式渲染到 DOM 上)
//方法一：行内样式
//方法二：类名样式


const style = {
  color: '#9Sf8OK',
  fontSize: '1.5rem'
}

//模板逻辑,尽量不放复杂的逻辑
function App() {
  return (
    <div className="App">
      {/* 👇 注意，这里有两个花括号！外层是为了让里边被识别为对象 */}

      {/* 🌟 不带变量的写法 */}
      <span style={{color: '#3370FF',fontSize: '1.5rem'}}>这是一个带有样式的 Span 标签</span> 

      {/* 🌟 带变量的写法（建议） */}
      <span style={{style}}>我是另一个带样式的 SPAN 标签</span> 


    </div>
  )
}

//最后需要 export 一下！
export default App;
