import "./App.css"
import React from "react"//引入 react 包


//🌟🌟通过类组件的方式 counter


class Counter extends React.Component {

  state = {
    count: 0
  }

  render() {
    return (
      <button>
        点赞数 + {this.state.count}
      </button>
    )
  }


}




//二：渲染类组件(跟组件)
function App () {
  return (
      <div>
        <Counter/>
      </div>

  )
}


//最后需要 export 一下！
export default App
