import "./App.css"
import React from 'react'
//1.从 react 中导入 useState,useEffect 函数
import { useRef } from 'react'


//🚀 Hook - useRef

//How?
//1.导入 useRef 函数
//2.执行 useRef 函数并传入 null，返回值作为一个对象，内部有个 current 属性来存放拿到的 DOM 对象 
//3.通过 ref 绑定元素


//场景一：获取组件实例 （函数组件没有实例，这里指的都是类组件）
//获取 DOM 对象 、标签

class TestC extends React.Component {
  render () {
    return (
      <div>我是一个类组件的元素</div>
    )
  }
}

function APP () {

  //

  return (
    <div>
      <TestC />
      <h1>这是 h1 元素</h1>
    </div>
  )
}

// 最后需要 export 一下！
export default APP
