import "./App.css"
import React from 'react'
//1.导入 useRef 函数
import { createContext,useState,useContext } from 'react'


//🚀 Hook - useContext
    //What?
      //函数组件之间的【数据通讯】

    //How?
    // 1.在爷爷组件内定义变量调用 createContext() 并解构赋值: const Context = createContext()
    // 2.在爷爷组件内【包裹】数据 <Context.Provider value={🍎}> ... </Context.Provider>
    // 3.在儿子组件内调用 useContext(Context)， 获取 Context 内容
    // 4.可以在爷爷组件内使用 useState() 方法去修改调用了爷爷组件的数据的父亲跟儿子组件！！🔥🔥



//1.解构赋值给 Context
const Context = createContext()






function APP () {

  //1.在爷爷组件内定义变量调用 createContext() 并解构赋值: const Context = createContext()
  const [count,setCount] = useState(0) //APP 爷爷组件内的数据

  return(
    //2.在爷爷组件内【包裹】数据 <Context.Provider value={🍎}> ... </Context.Provider>
      <Context.Provider value={count}>
        <div>
            <ComA />
            {/* 4.🔥🔥相当于爷爷组件去控制父组件跟子组件的信息！因为下面的数据都是来自 APP 的 */}
            <button onClick={()=> {setCount(count+1)} }> + </button>
        </div>
      </Context.Provider>
  )
}




function ComA() {

  const count = useContext(Context)
  return(
    <>
      <div>从 APP 传过来的数据👉: {count} </div>
      <br />
      <ComB />
    </>
  )
}



function ComB() {

  const count = useContext(Context)
  return(
    <div>从 APP 来的👉: {count} </div>
  )
}





// 最后需要 export 一下！
export default APP
