import "./App.css"
import React from 'react'
//1.从 react 中导入 useState,useEffect 函数
import { useState,useEffect } from 'react'


//🚀 Hook - useEffect 清除副作用



function Timer(){
//把定时器的结果嵌入到 html 内 
  const [time,setTime] = useState(0)


  useEffect(()=>{
    //设置定时器
    let timer = setInterval(()=>{//更新时间
      setTime(time+1)
    },1000)

    return ()=>{
      //在 return 一个【清理时间】的动作

    }

  },[])

  return (
    <div>
      <h1>{time}</h1>
    </div>
  )

}


function APP(){

  const [flag,setFlag] = useState(true) //flag 为 true 的话，就开始定时器
  return(
    <div>
      {flag ? <Timer /> : null}
      <button onClick={() =>setFlag(!flag)}> 函数的销毁&重建 </button>
    </div>
  )
}

// 最后需要 export 一下！
export default APP
