import "./App.css"

//组件化：函数组件 & 类组件


//一：创建函数组件
function hello() {
  return <div>hello</div> //当前组件要渲染的 html 模板是什么
}


//二：渲染函数组件
function App () {
  return (
    < >
      <div>
          {/* 自闭合标签 */}
          <hello/> 
          <hello/> 
          {/* 函数式标签 */}
          <hello></hello>
          <hello/> 
      </div>

    </>
  )
}

//最后需要 export 一下！
export default App
