import "./App.css"
import React from 'react'
//1.导入 useRef 函数
import { createContext,useState,useContext } from 'react'


//🚀 Hook - useContext
    //What?
      //函数组件之间的【数据通讯】

    //How?
    // 1.在爷爷组件内定义变量调用 createContext() 并解构赋值: const Context = createContext()
    // 2.在爷爷组件内【包裹】数据 <Context.Provider value={🍎}> ... </Context.Provider>
    // 3.在儿子组件内调用 useContext(Context)， 获取 Context 内容



//1.解构赋值给 Context
const Context = createContext()






function APP () {

  const [count,setCount] = useState('来自 APP 的数据') //APP 内的数据
  return(
      <Context.Provider value={count}>
        <div>
            <ComA />
            <br />
        </div>
      </Context.Provider>
  )
}




function ComA() {

  const count = useContext(Context)
  return(
    <div>从 APP 传过来的数据👉: {count}
      <ComB />
    </div>
  )
}



function ComB() {

  const count = useContext(Context)
  return(
    <div>从 APP 来的👉: {count} </div>
  )
}





// 最后需要 export 一下！
export default APP
