//🍎子组件：渲染列表结构
function ListItem( {children} ){
  return (
    <>
    <h3>小标题</h3>
    <p>
      啦啦啦,
      {children}
    </p>
    </>
  )
}

//🍎父组件：提供数据
class Data extends React.Component {

  render() {
    return (

      <div>
            <ListItem>
              {/* 当组件标签【内部写了东西后】，就会自动生成一个 children 属性 */}
              这是 children 属性

              <div>这是 child</div>

              { ()=>console.log }

            </ListItem>
      </div>
    )
  }

};

// //——————————————————————


// //最后需要 export 一下！
export default Data
